import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import AppLayout from "../components/AppLayout";
import PostCard from "../components/PostCard";
import PostFrom from "../components/PostForm";
import { LOAD_POSTS_REQUEST } from "../reducers/post";

const Home = () => {
  const { self } = useSelector((state) => state.user);
  const { mainPosts, hasMorePosts } = useSelector((state) => state.post);

  const dispatch = useDispatch();
  useEffect(() => {
    dispatch({
      type: LOAD_POSTS_REQUEST,
    });
  }, []);

  useEffect(() => {
    function onScroll() {
      // console.log(
      //   //NOTE: scrollHeight=scrollY+clientHeight
      //   window.scrollY, //현재 스크롤의 위치값
      //   document.documentElement.clientHeight, //화면의 높이
      //   document.documentElement.scrollHeight //scrollHeight는 현재 스크롤을 내릴수 있는 높이
      // );
      if (
        window.scrollY + document.documentElement.clientHeight ===
        document.documentElement.scrollHeight - 300
      ) {
        if (hasMorePosts) {
          dispatch({
            type: LOAD_POSTS_REQUEST,
          });
        }
      }
    }
    window.addEventListener("scroll", onScroll);

    return () => {
      window.removeEventListener("scroll", onScroll);
    };
  }, [hasMorePosts]);

  return (
    <>
      <AppLayout>
        {self && <PostFrom />}
        {mainPosts.map((post) => (
          <PostCard key={post.id} post={post} />
        ))}
      </AppLayout>
    </>
  );
};

export default Home;
