import React, { useState } from "react";
import PropTypes from "prop-types";
import Slick from "react-slick";
import {
  OverLay,
  Global,
  Header,
  CloseBtn,
  ImgWrapper,
  Indicator,
  SlickWrapper,
} from "./styles";

