const initalState = {
  user: {
    loginFl: false,
    user: null,
    signUpDate: {},
    loginData: {},
  },
  post: {
    mainPosts: [],
  },
};

// const changeNickname = {
//   type: "CHANGE_NICKNAME",
//   data: "wooseop",
// };

//같은 액션 데이터만 동적으로 바꾸는 함수 , action creator
// const changeNickname = (data) => {
//   return {
//     type: "CHANGE_NICKNAME",
//     data,
//   };
// };

export const loginAction = (data) => {
  return {
    type: "LOG_IN",
    data,
  };
};
export const logoutAction = () => {
  return {
    type: "LOG_OUT",
  };
};

//NOTE:(이전상태, 액션) => return 다음상태
const rootReducer = (state = initalState, action) => {
  switch (action.type) {
    case "LOG_IN":
      return {
        ...state,
        user: { ...state.user, loginFl: true, user: action.data },
      };
    case "LOG_OUT":
      return {
        ...state,
        user: { ...state.user, loginFl: false, user: null },
      };

    default:
      break;
  }
};

export default rootReducer;
