export const loginAction = (data) => {
  return {
    type: "LOG_IN",
    data,
  };
};
export const logoutAction = () => {
  return {
    type: "LOG_OUT",
  };
};

const reducer = (state = initalState, action) => {
  switch (action.type) {
    case "LOG_IN":
      return {
        ...state,
        user: { ...state, loginFl: true, user: action.data },
      };
    case "LOG_OUT":
      return {
        ...state,
        user: { ...state, loginFl: false, user: null },
      };

    default:
      return state;
  }
};

export default reducer;
